          const existingSubscriber = await db.subscriber.findUnique({
            where: { email: email.toLowerCase() }
          })

          if (existingSubscriber) {
            if (existingSubscriber.status === 'UNSUBSCRIBED') {
              // Reactivate unsubscribed user
              await db.subscriber.update({
                where: { id: existingSubscriber.id },
                data: { 
                  status: 'ACTIVE',
                  updatedAt: new Date()
                }
              })
              return jsonResponse({ 
                message: 'Welcome back! Your subscription has been reactivated.',
                subscriber: { email: existingSubscriber.email }
              })
            } else {
              return jsonResponse({ 
                message: 'You are already subscribed to our newsletter.' 
              }, 409)
            }
          }

          // Create new subscriber
          const subscriber = await db.subscriber.create({
            data: {
              email: email.toLowerCase(),
              status: 'ACTIVE',
              preferences: null
            }
          })

          return jsonResponse({ 
            message: 'Successfully subscribed! Welcome to AI Product Newsletter.',
            subscriber: { email: subscriber.email }
          }, 201)

        } catch (error) {
          console.error('Subscribe error:', error)
          return jsonResponse({ 
            error: 'Failed to process subscription' 
          }, 500)
        }
      }

      // Unsubscribe endpoint
      if (path === '/api/unsubscribe' && method === 'POST') {
        try {
          const body = await request.json()
          const { email } = body

          if (!email || !isValidEmail(email)) {
            return jsonResponse({ 
              error: 'Valid email address is required' 
            }, 400)
          }

          // Find and update subscriber
          const subscriber = await db.subscriber.findUnique({
            where: { email: email.toLowerCase() }
          })

          if (!subscriber) {
            return jsonResponse({ 
              error: 'Email address not found in our database' 
            }, 404)
          }

          if (subscriber.status === 'UNSUBSCRIBED') {
            return jsonResponse({ 
              message: 'You are already unsubscribed.' 
            })
          }

          // Update subscriber status
          await db.subscriber.update({
            where: { id: subscriber.id },
            data: { 
              status: 'UNSUBSCRIBED',
              updatedAt: new Date()
            }
          })

          return jsonResponse({ 
            message: 'Successfully unsubscribed. Sorry to see you go!' 
          })

        } catch (error) {
          console.error('Unsubscribe error:', error)
          return jsonResponse({ 
            error: 'Failed to process unsubscription' 
          }, 500)
        }
      }

      // 404 for unknown routes
      return jsonResponse({ 
        error: 'Not found' 
      }, 404)

    } catch (error) {
      console.error('Request error:', error)
      return jsonResponse({ 
        error: 'Internal server error' 
      }, 500)
    }
  },

  // Scheduled event handler for cron jobs
  async scheduled(event: ScheduledEvent, env: Env): Promise<void> {
