// API endpoint handlers
import { handleSubscribe } from './api/subscribe'
import { handleUnsubscribe } from './api/unsubscribe'
import { handleHealth } from './api/health'

// Cron job handlers
import { generateWeeklyNewsletter } from './cron/newsletter-generator'

// Shared utilities
import { getPrismaClient } from './shared/database'
import { handleCORS, jsonResponse } from './shared/cors'

export interface Env {
  DATABASE_URL: string
}

export default {
  async fetch(request: Request, env: Env): Promise<Response> {
    // Handle CORS preflight
    const corsResponse = handleCORS(request)
    if (corsResponse) return corsResponse

    const url = new URL(request.url)
    const path = url.pathname
    const method = request.method

    try {
      // Initialize Prisma client
      const db = getPrismaClient(env.DATABASE_URL)

      // Health check endpoint
      if (path === '/api/health' && method === 'GET') {
        const result = await handleHealth()
        return jsonResponse(result.data, result.status)
      }

      // Subscribe endpoint
      if (path === '/api/subscribe' && method === 'POST') {
        const body = await request.json()
        const result = await handleSubscribe(body, db)
        return jsonResponse(result.data, result.status)
      }

      // Unsubscribe endpoint
      if (path === '/api/unsubscribe' && method === 'POST') {
        const body = await request.json()
        const result = await handleUnsubscribe(body, db)
        return jsonResponse(result.data, result.status)
      }

      // 404 for unknown routes
      return jsonResponse({ 
        error: 'Not found' 
      }, 404)

    } catch (error) {
      console.error('Request error:', error)
      return jsonResponse({ 
        error: 'Internal server error' 
      }, 500)
    }
  },

  // Scheduled event handler for cron jobs
  async scheduled(event: ScheduledEvent, env: Env): Promise<void> {
    console.log('Cron job triggered:', event.cron)

    try {
      const db = getPrismaClient(env.DATABASE_URL)
      const result = await generateWeeklyNewsletter(db)

      console.log('Newsletter generation result:', result)

    } catch (error) {
      console.error('Scheduled task error:', error)
    }
  }
}

