import { PrismaClient } from '@prisma/client'

export interface SubscribeRequest {
  email: string
  preferences?: any
}

export interface SubscribeResponse {
  message: string
  subscriber?: { email: string }
  error?: string
}

export function isValidEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
  return emailRegex.test(email)
}

export async function handleSubscribe(
  request: SubscribeRequest, 
  db: PrismaClient
): Promise<{ data: SubscribeResponse; status: number }> {

  const { email, preferences } = request

  // Validate email
  if (!email || !isValidEmail(email)) {
    return {
      data: { error: 'Valid email address is required' },
      status: 400
    }
  }

  try {
    // Check if subscriber already exists
    const existingSubscriber = await db.subscriber.findUnique({
      where: { email: email.toLowerCase() }
    })

    if (existingSubscriber) {
      if (existingSubscriber.status === 'UNSUBSCRIBED') {
        // Reactivate unsubscribed user
        await db.subscriber.update({
          where: { id: existingSubscriber.id },
          data: { 
            status: 'ACTIVE',
            preferences: preferences || existingSubscriber.preferences,
            updatedAt: new Date()
          }
        })
        return {
          data: { 
            message: 'Welcome back! Your subscription has been reactivated.',
            subscriber: { email: existingSubscriber.email }
          },
          status: 200
        }
      } else {
        return {
          data: { message: 'You are already subscribed to our newsletter.' },
          status: 409
        }
      }
    }

    // Create new subscriber
    const subscriber = await db.subscriber.create({
      data: {
        email: email.toLowerCase(),
        status: 'ACTIVE',
        preferences: preferences || null
      }
    })

    return {
      data: { 
        message: 'Successfully subscribed! Welcome to AI Product Newsletter.',
        subscriber: { email: subscriber.email }
      },
      status: 201
    }

  } catch (error) {
    console.error('Subscribe error:', error)
    return {
      data: { error: 'Failed to process subscription' },
      status: 500
    }
  }
}

