import { PrismaClient } from '@prisma/client'
import { SubscribeRequest, SubscribeResponse, UnsubscribeRequest, UnsubscribeResponse } from '../types/api.js'
import { ConflictError, NotFoundError } from '../types/errors.js'
import prisma from '../utils/database.js'

export class SubscriberService {
  private db: PrismaClient

  constructor(database: PrismaClient = prisma) {
    this.db = database
  }

  // Subscribe to newsletter (migrated from workers/api/subscribe.ts)
  async subscribe({ email, preferences }: SubscribeRequest): Promise<SubscribeResponse> {
    // Check if subscriber already exists
    const existingSubscriber = await this.db.subscriber.findUnique({
      where: { email }
    })

    if (existingSubscriber) {
      if (existingSubscriber.status === 'UNSUBSCRIBED') {
        // Reactivate unsubscribed user
        await this.db.subscriber.update({
          where: { id: existingSubscriber.id },
          data: { 
            status: 'ACTIVE',
            preferences: preferences || existingSubscriber.preferences,
            updatedAt: new Date()
          }
        })

        return { 
          message: 'Welcome back! Your subscription has been reactivated.',
          subscriber: { email: existingSubscriber.email }
        }
      } else {
        throw new ConflictError('You are already subscribed to our newsletter.')
      }
    }

    // Create new subscriber
    const subscriber = await this.db.subscriber.create({
      data: {
        email,
        status: 'ACTIVE',
        preferences: preferences || null
      }
    })

    return { 
      message: 'Successfully subscribed! Welcome to AI Product Newsletter.',
      subscriber: { email: subscriber.email }
    }
  }

  // Unsubscribe from newsletter (migrated from workers/api/unsubscribe.ts)
  async unsubscribe({ email }: UnsubscribeRequest): Promise<UnsubscribeResponse> {
    // Find subscriber
    const subscriber = await this.db.subscriber.findUnique({
      where: { email }
    })

    if (!subscriber) {
      throw new NotFoundError('Email address not found in our database')
    }

    if (subscriber.status === 'UNSUBSCRIBED') {
      return { message: 'You are already unsubscribed.' }
    }

    // Update subscriber status
    await this.db.subscriber.update({
      where: { id: subscriber.id },
      data: { 
        status: 'UNSUBSCRIBED',
        updatedAt: new Date()
      }
    })

    return { message: 'Successfully unsubscribed. Sorry to see you go!' }
  }

  // Get subscriber by email
  async getSubscriberByEmail(email: string) {
    return await this.db.subscriber.findUnique({
      where: { email }
    })
  }

  // Get active subscribers count
  async getActiveSubscribersCount(): Promise<number> {
    return await this.db.subscriber.count({
      where: { status: 'ACTIVE' }
    })
  }

  // Get all active subscribers
  async getActiveSubscribers() {
    return await this.db.subscriber.findMany({
      where: { status: 'ACTIVE' },
      select: {
        id: true,
        email: true,
        preferences: true,
        createdAt: true,
        updatedAt: true
      }
    })
  }
}

