import cron from 'node-cron'
import { NewsletterService } from '../services/newsletterService.js'
import { config } from '../utils/config.js'

export class NewsletterJob {
  private newsletterService: NewsletterService
  private task: cron.ScheduledTask | null = null

  constructor() {
    this.newsletterService = new NewsletterService()
  }

  // Start the scheduled job
  start(): void {
    // Run every Monday at 9:00 AM UTC (same as Workers cron: "0 9 * * MON")
    const cronSchedule = '0 9 * * MON'

    console.log(`ğŸ“… Scheduling newsletter generation job: ${cronSchedule}`)

    this.task = cron.schedule(cronSchedule, async () => {
      console.log('â° Newsletter generation job triggered')
      await this.executeJob()
    }, {
      scheduled: true,
      timezone: 'UTC'
    })

    console.log('âœ… Newsletter job scheduled successfully')
  }

  // Stop the scheduled job
  stop(): void {
    if (this.task) {
      this.task.stop()
      this.task = null
      console.log('ğŸ›‘ Newsletter job stopped')
    }
  }

  // Execute the job manually (for testing)
  async executeManually(): Promise<void> {
    console.log('ğŸ”§ Manually executing newsletter generation job')
    await this.executeJob()
  }

  // Private method to execute the actual job
  private async executeJob(): Promise<void> {
    try {
      const result = await this.newsletterService.generateWeeklyNewsletter()
      console.log('ğŸ“§ Newsletter generation result:', result)

      if (result.success) {
        console.log(`âœ… Successfully generated newsletter for ${result.subscriberCount} subscribers`)
      } else {
        console.error('âŒ Newsletter generation failed:', result.message)
      }
    } catch (error) {
      console.error('ğŸ’¥ Newsletter job execution failed:', error)
    }
  }

  // Get job status
  getStatus(): { running: boolean; nextExecution?: Date } {
    return {
      running: this.task !== null && this.task.running,
      ...(this.task && { nextExecution: this.task.nextDate() })
    }
  }
}

// Singleton instance
export const newsletterJob = new NewsletterJob()

