import { newsletterJob } from './newsletterJob.js'

// Job registry for managing all scheduled jobs
export class JobManager {
  private jobs: Map<string, any> = new Map()

  constructor() {
    // Register all jobs
    this.jobs.set('newsletter', newsletterJob)
  }

  // Start all jobs
  startAll(): void {
    console.log('ğŸš€ Starting all scheduled jobs...')

    this.jobs.forEach((job, name) => {
      try {
        job.start()
        console.log(`âœ… Started job: ${name}`)
      } catch (error) {
        console.error(`âŒ Failed to start job ${name}:`, error)
      }
    })

    console.log(`ğŸ“‹ Started ${this.jobs.size} scheduled jobs`)
  }

  // Stop all jobs
  stopAll(): void {
    console.log('ğŸ›‘ Stopping all scheduled jobs...')

    this.jobs.forEach((job, name) => {
      try {
        job.stop()
        console.log(`ğŸ›‘ Stopped job: ${name}`)
      } catch (error) {
        console.error(`âŒ Failed to stop job ${name}:`, error)
      }
    })

    console.log('ğŸ All scheduled jobs stopped')
  }

  // Get job by name
  getJob(name: string) {
    return this.jobs.get(name)
  }

  // Get status of all jobs
  getJobStatuses(): Record<string, any> {
    const statuses: Record<string, any> = {}

    this.jobs.forEach((job, name) => {
      statuses[name] = job.getStatus ? job.getStatus() : { status: 'unknown' }
    })

    return statuses
  }

  // Manual job execution
  async executeJob(name: string): Promise<void> {
    const job = this.jobs.get(name)
    if (!job) {
      throw new Error(`Job '${name}' not found`)
    }

    if (job.executeManually) {
      await job.executeManually()
    } else {
      throw new Error(`Job '${name}' does not support manual execution`)
    }
  }
}

// Singleton instance
export const jobManager = new JobManager()

// Graceful shutdown handler
process.on('SIGINT', () => {
  console.log('\nğŸ”„ Gracefully shutting down scheduled jobs...')
  jobManager.stopAll()
  process.exit(0)
})

process.on('SIGTERM', () => {
  console.log('\nğŸ”„ Gracefully shutting down scheduled jobs...')
  jobManager.stopAll()
  process.exit(0)
})

