import { Request, Response, NextFunction } from 'express'
import { config } from '../utils/config.js'

// Response formatter middleware for consistent API responses
export const responseFormatter = (req: Request, res: Response, next: NextFunction): void => {
  // Success response helper
  res.success = (data: any, metadata = {}): void => {
    res.status(200).json({
      status: 'success',
      ...data, // Spread data directly (not wrapped)
      ...metadata, // Include metadata if needed
      timestamp: new Date().toISOString()
    })
  }

  // Error response helper
  res.error = (message: string, code = 400, details = {}): void => {
    res.status(code).json({
      status: 'error',
      error: {
        message,
        code,
        ...(Object.keys(details).length > 0 && { details }),
        timestamp: new Date().toISOString()
      }
    })
  }

  // Generic API response helper (maintains existing pattern)
  res.apiResponse = (data: any, status = 200): void => {
    res.status(status).json({
      status: status >= 200 && status < 300 ? 'success' : 'error',
      data,
      timestamp: new Date().toISOString()
    })
  }

  next()
}

// Response logging middleware (optional, for development)
export const responseLogger = (req: Request, res: Response, next: NextFunction): void => {
  if (config.NODE_ENV === 'development') {
    const originalJson = res.json

    res.json = function(body: any) {
      console.log(`ğŸ“¤ Response [${req.method} ${req.path}]:`, {
        status: res.statusCode,
        body: typeof body === 'object' ? JSON.stringify(body, null, 2) : body
      })
      return originalJson.call(this, body)
    }
  }

  next()
}

