import { Request, Response, NextFunction } from 'express'
import { SubscriberService } from '../services/subscriberService.js'
import { subscribeSchema, unsubscribeSchema } from '../utils/validation.js'
import { asyncHandler } from '../middleware/errorHandler.js'

export class SubscriberController {
  private subscriberService: SubscriberService

  constructor() {
    this.subscriberService = new SubscriberService()
  }

  // Subscribe to newsletter
  subscribe = asyncHandler(async (req: Request, res: Response) => {
    const validatedData = subscribeSchema.parse(req.body)
    const result = await this.subscriberService.subscribe(validatedData)

    res.status(201).json({
      status: 'success',
      data: result,
      timestamp: new Date().toISOString()
    })
  })

  // Unsubscribe from newsletter
  unsubscribe = asyncHandler(async (req: Request, res: Response) => {
    const validatedData = unsubscribeSchema.parse(req.body)
    const result = await this.subscriberService.unsubscribe(validatedData)

    res.status(200).json({
      status: 'success',
      data: result,
      timestamp: new Date().toISOString()
    })
  })

  // Get subscriber stats (for admin use)
  getStats = asyncHandler(async (req: Request, res: Response) => {
    const count = await this.subscriberService.getActiveSubscribersCount()

    res.status(200).json({
      status: 'success',
      data: {
        activeSubscribers: count
      },
      timestamp: new Date().toISOString()
    })
  })
}

