import { Request, Response, NextFunction } from 'express'
import { ApiError, PrismaError } from '../types/errors.js'
import { config, isProduction } from '../utils/config.js'

// Prisma error code mappings
const PRISMA_ERROR_CODES = {
  P2002: 'Unique constraint failed',
  P2003: 'Foreign key constraint failed', 
  P2004: 'Constraint failed',
  P2005: 'Invalid value stored',
  P2006: 'Invalid value provided',
  P2007: 'Data validation error',
  P2008: 'Failed to parse query',
  P2009: 'Failed to validate query',
  P2010: 'Raw query failed',
  P2011: 'Null constraint violation',
  P2012: 'Missing required value',
  P2013: 'Missing required argument',
  P2014: 'Required relation is missing',
  P2015: 'Related record not found',
  P2016: 'Query interpretation error',
  P2017: 'Records not connected',
  P2018: 'Required connected records not found',
  P2019: 'Input error',
  P2020: 'Value out of range',
  P2021: 'Table does not exist',
  P2022: 'Column does not exist',
  P2025: 'Record to delete does not exist'
}

// Handle Prisma errors and convert to ApiError
function handlePrismaError(error: PrismaError): ApiError {
  if (!error.code) {
    return new ApiError('Database operation failed', 500)
  }

  const errorMessage = PRISMA_ERROR_CODES[error.code as keyof typeof PRISMA_ERROR_CODES] 
    || 'Database operation failed'

  switch (error.code) {
    case 'P2002': // Unique constraint
      const target = error.meta?.target || ['field']
      return new ApiError(
        `${errorMessage}: ${target.join(', ')} must be unique`,
        409,
        true,
        { field: target[0], type: 'unique_constraint' }
      )

    case 'P2003': // Foreign key constraint  
      const field = error.meta?.field_name || 'unknown field'
      return new ApiError(
        `${errorMessage} on field: ${field}`,
        400,
        true,
        { field, type: 'foreign_key_constraint' }
      )

    case 'P2025': // Record not found
      return new ApiError('Record not found', 404)

    case 'P2011': // Null constraint violation
    case 'P2012': // Missing required value  
    case 'P2013': // Missing required argument
      return new ApiError('Missing required field', 400, true, { type: 'missing_field' })

    case 'P2006': // Invalid value provided
    case 'P2007': // Data validation error  
    case 'P2019': // Input error
    case 'P2020': // Value out of range
      return new ApiError('Invalid data provided', 400, true, { type: 'validation_error' })

    default:
      return new ApiError(errorMessage, 500)
  }
}

// Log errors in development
function logError(error: Error, req: Request) {
  if (!isProduction()) {
    console.error(`\nâŒ Error in ${req.method} ${req.path}:`)
    console.error(`Message: ${error.message}`)
    console.error(`Stack: ${error.stack}`)

    if (error instanceof ApiError && error.details) {
      console.error(`Details:`, error.details)
    }
  }
}

// Main error handling middleware
export const errorHandler = (
  error: Error | ApiError | PrismaError,
  req: Request,
  res: Response,
  next: NextFunction
): void => {
  logError(error, req)

  // Handle ApiError instances
  if (error instanceof ApiError) {
    res.status(error.statusCode).json({
      status: 'error',
      message: error.message,
      ...(error.details && { details: error.details }),
      ...(isProduction() && { timestamp: new Date().toISOString() })
    })
    return
  }

  // Handle Prisma errors
  if (error.name === 'PrismaClientKnownRequestError') {
    const apiError = handlePrismaError(error as PrismaError)
    res.status(apiError.statusCode).json({
      status: 'error',
      message: apiError.message,
      ...(apiError.details && { details: apiError.details }),
      timestamp: new Date().toISOString()
    })
    return
  }

  // Handle other Prisma errors
  if (error.name === 'PrismaClientUnknownRequestError') {
    res.status(500).json({
      status: 'error',
      message: 'Unknown database error',
      timestamp: new Date().toISOString()
    })
    return
  }

  if (error.name === 'PrismaClientValidationError') {
    res.status(400).json({
      status: 'error',
      message: 'Invalid query parameters',
      timestamp: new Date().toISOString()
    })
    return
  }

  // Handle validation errors (Zod, etc.)
  if (error.name === 'ZodError') {
    res.status(400).json({
      status: 'error',
      message: 'Validation failed',
      details: (error as any).issues,
      timestamp: new Date().toISOString()
    })
    return
  }

  // Handle unexpected errors
  console.error('Unexpected error:', error)
  res.status(500).json({
    status: 'error',
    message: isProduction() ? 'Internal server error' : error.message,
    ...((!isProduction() && error.stack) && { stack: error.stack }),
    timestamp: new Date().toISOString()
  })
}

// Async error wrapper for route handlers
export const asyncHandler = (fn: Function) => {
  return (req: Request, res: Response, next: NextFunction) => {
    Promise.resolve(fn(req, res, next)).catch(next)
  }
}

// 404 handler for unmatched routes
export const notFoundHandler = (req: Request, res: Response) => {
  res.status(404).json({
    status: 'error',
    message: `Route ${req.method} ${req.path} not found`,
    timestamp: new Date().toISOString()
  })
}

