import express from 'express'
import helmet from 'helmet'
import compression from 'compression'
import morgan from 'morgan'
import { config, isDevelopment } from './utils/config.js'
import { corsMiddleware } from './middleware/cors.js'
import { errorHandler, notFoundHandler } from './middleware/errorHandler.js'
import { jobManager } from './jobs/index.js'
import routes from './routes/index.js'
import { checkDatabaseConnection } from './utils/database.js'

// Create Express app
const app = express()

// Security middleware
app.use(helmet({
  crossOriginEmbedderPolicy: false // Allow frontend integration
}))

// Enable compression
app.use(compression())

// CORS handling
app.use(corsMiddleware)

// Body parsing middleware
app.use(express.json({ limit: '10mb' }))
app.use(express.urlencoded({ extended: true, limit: '10mb' }))

// Logging middleware
if (isDevelopment()) {
  app.use(morgan('dev'))
} else {
  app.use(morgan('combined'))
}

// Trust proxy for production deployment
if (!isDevelopment()) {
  app.set('trust proxy', 1)
}

// API routes
app.use('/api', routes)

// Health check at root
app.get('/', (req, res) => {
  res.json({
    status: 'success',
    message: 'AI Product Newsletter API',
    version: '1.0.0',
    timestamp: new Date().toISOString()
  })
})

// 404 handler for unmatched routes
app.use(notFoundHandler)

// Global error handler (must be last)
app.use(errorHandler)

// Database connection check and server startup
async function startServer() {
  try {
    // Check database connection
    console.log('ğŸ” Checking database connection...')
    const dbConnected = await checkDatabaseConnection()

    if (!dbConnected) {
      console.error('âŒ Database connection failed')
      process.exit(1)
    }

    console.log('âœ… Database connection established')

    // Start scheduled jobs
    console.log('ğŸ“… Starting scheduled jobs...')
    jobManager.startAll()

    // Start server
    const server = app.listen(config.PORT, () => {
      console.log('')
      console.log('ğŸš€ AI Product Newsletter API Server Started')
      console.log('======================================')
      console.log(`ğŸ“ Environment: ${config.NODE_ENV}`)
      console.log(`ğŸŒ Port: ${config.PORT}`)
      console.log(`ğŸ”— URL: http://localhost:${config.PORT}`)
      console.log(`ğŸ’» Health Check: http://localhost:${config.PORT}/api/health`)
      console.log('')

      if (isDevelopment()) {
        console.log('ğŸ“‹ Available endpoints:')
        console.log('  GET    /api/health')
        console.log('  POST   /api/subscribe')
        console.log('  POST   /api/unsubscribe')
        console.log('  GET    /api/subscribers/stats')
        console.log('')
      }
    })

    // Graceful shutdown
    const shutdown = () => {
      console.log('\nğŸ”„ Received shutdown signal, gracefully shutting down...')

      server.close(() => {
        console.log('ğŸ›‘ HTTP server closed')
        jobManager.stopAll()
        process.exit(0)
      })

      // Force shutdown after 10 seconds
      setTimeout(() => {
        console.error('âŒ Could not close connections in time, forcefully shutting down')
        process.exit(1)
      }, 10000)
    }

    process.on('SIGTERM', shutdown)
    process.on('SIGINT', shutdown)

  } catch (error) {
    console.error('ğŸ’¥ Failed to start server:', error)
    process.exit(1)
  }
}

// Start the server
startServer()

export default app

