// Example demonstrating the consistent response formatter usage
import { Request, Response } from 'express'

// Example controller showing different response patterns
export class ExampleController {

  // Success response with data
  successExample = (req: Request, res: Response) => {
    const userData = {
      message: 'Successfully subscribed!',
      subscriber: { email: 'user@example.com' }
    }

    // Using the response formatter
    res.success(userData)
    // Results in:
    // {
    //   "status": "success",
    //   "message": "Successfully subscribed!",
    //   "subscriber": { "email": "user@example.com" },
    //   "timestamp": "2024-08-26T21:50:00.000Z"
    // }
  }

  // Success response with metadata
  successWithMetadata = (req: Request, res: Response) => {
    const listData = {
      subscribers: [
        { email: 'user1@example.com' },
        { email: 'user2@example.com' }
      ]
    }

    const metadata = {
      pagination: {
        page: 1,
        limit: 10,
        total: 2
      }
    }

    res.success(listData, metadata)
    // Results in:
    // {
    //   "status": "success",
    //   "subscribers": [...],
    //   "pagination": { "page": 1, "limit": 10, "total": 2 },
    //   "timestamp": "2024-08-26T21:50:00.000Z"
    // }
  }

  // Error response
  errorExample = (req: Request, res: Response) => {
    const validationDetails = {
      field: 'email',
      issue: 'Invalid format'
    }

    res.error('Validation failed', 400, validationDetails)
    // Results in:
    // {
    //   "status": "error",
    //   "error": {
    //     "message": "Validation failed",
    //     "code": 400,
    //     "details": {
    //       "field": "email",
    //       "issue": "Invalid format"
    //     },
    //     "timestamp": "2024-08-26T21:50:00.000Z"
    //   }
    // }
  }

  // Alternative API response (maintains existing pattern)
  apiResponseExample = (req: Request, res: Response) => {
    const data = { message: 'Health check passed' }

    res.apiResponse(data, 200)
    // Results in:
    // {
    //   "status": "success",
    //   "data": { "message": "Health check passed" },
    //   "timestamp": "2024-08-26T21:50:00.000Z"
    // }
  }
}

/* 
Response Format Benefits:

1. CONSISTENCY: All responses follow the same structure
2. TYPE SAFETY: TypeScript extensions provide autocomplete
3. CLEAN CODE: Controllers focus on business logic, not response formatting
4. FLEXIBILITY: Support for both simple and complex response patterns
5. DEBUGGING: Automatic timestamps and standardized error format
6. FUTURE-PROOF: Easy to extend with additional metadata fields
*/

