import { Request, Response } from 'express'

// Example controller showing different response patterns
export class ExampleController {

  // Success response with data
  successExample = (req: Request, res: Response) => {
    const userData = {
      message: 'Successfully subscribed!',
      subscriber: { email: 'user@example.com' }
    }

    // Using the response formatter
    res.success(userData)
    // Results in:
    // {
    //   "status": "success",
    //   "message": "Successfully subscribed!",
    //   "subscriber": { "email": "user@example.com" },
    //   "timestamp": "2024-08-26T21:50:00.000Z"
    // }
  }

  // Success response with metadata
  successWithMetadata = (req: Request, res: Response) => {
    const listData = {
      subscribers: [
        { email: 'user1@example.com' },
        { email: 'user2@example.com' }
      ]
    }

    const metadata = {
      pagination: {
        page: 1,
        limit: 10,
        total: 2
      }
    }

    res.success(listData, metadata)
    // Results in:
    // {
    //   "status": "success",
    //   "subscribers": [...],
    //   "pagination": { "page": 1, "limit": 10, "total": 2 },
    //   "timestamp": "2024-08-26T21:50:00.000Z"
    // }
  }

  // Error response
  errorExample = (req: Request, res: Response) => {
    const validationDetails = {
      field: 'email',
      issue: 'Invalid format'
    }

    res.error('Validation failed', 400, validationDetails)
    // Results in:
    // {
    //   "status": "error",
    //   "error": {
    //     "message": "Validation failed",
    //     "code": 400,
    //     "details": {
